func TestHandleRequest(t *testing.T) {
	// Создаем фейковый HTTP запрос
	req, err := http.NewRequest("GET", "/example", nil)
	assert.Nil(t, err, "Should not return an error")

	// Создаем фейковый HTTP ResponseWriter с помощью httptest
	w := httptest.NewRecorder()

	// Вызываем обработчик с фейковым запросом
	HandleRequest(w, req)

	// Проверяем код статуса
	assert.Equal(t, http.StatusOK, w.Code, "Should return status 200")

	// Проверяем тело ответа
	assert.Equal(t, "Hello, World!", w.Body.String(), "Response body should match")
}

